package ma;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.SearchView;
import androidx.core.view.MenuItemCompat;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.Menu;
import android.view.MenuItem;

import java.util.ArrayList;
import java.util.List;

import ma.ensa.adapter.StarAdapter;
import ma.ensa.service.StarService;
import ma.ensa.list.R;
import ma.ensa.beans.Star;


public class ListActivity extends AppCompatActivity {
    private List<Star> stars;
    private RecyclerView recyclerView;
    private StarAdapter starAdapter = null;

    private StarService service;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list);
        stars = new ArrayList<>();
        service = StarService.getInstance();
        init();
        recyclerView = findViewById(R.id.recycle_view);
        starAdapter = new StarAdapter(this, service.findAll());
        recyclerView.setAdapter(starAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu, menu);
        MenuItem menuItem = menu.findItem(R.id.app_bar_search);
        SearchView searchView = (SearchView) MenuItemCompat.getActionView(menuItem);
        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                return true;
            }
            @Override
            public boolean onQueryTextChange(String newText) {
                if (starAdapter != null){
                    starAdapter.getFilter().filter(newText);
                }
                return true;
            }
        });
        return true;
    }

    public void init() {
        service.create(new Star("Lucifer ", "https://static.wikia.nocookie.net/lucifer/images/8/8a/Lucifer_Morningstar.jpg/revision/latest/scale-to-width-down/1200?cb=20171210174001&path-prefix=fr", 3.5f));
        service.create(new Star("Deepika padukone", "data:image/jpeg;base64,", 4));
        service.create(new Star("Selena Gomez", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQylmlgmhKhaf6ep0-CqBw6i2LGgvDaQp-jFnjlcRq5LYMBTbLCupbVgIcCd4trGVvbXgE&usqp=CAU", 5));
        service.create(new Star("Lucas Bravo", "https://people.com/thmb/MD9zN9WK4v8ZiB97GStPyApxXjw=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc():focal(663x0:665x2)/lucas-bravo-publicity-2000-b1a7f85010d44a83a8b77dbfe0c43529.jpeg", 1));
        service.create(new Star("safaa el harras", "https://media.licdn.com/dms/image/D4E03AQHxu0hndIIkGg/profile-displayphoto-shrink_800_800/0/1684329162145?e=1703116800&v=beta&t=NLQCm9xtz8OIq5DJ6xWDjVDeU-qKkZFUoCNxlng960o", 5));
        service.create(new Star("The weekend", "data:image/jpeg;base64,", 1));
    }
}